import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  // Static cocktails for the home page cards
cocktails = [
  { name: 'Mojito', icon: 'üåø', gradient: 'linear-gradient(135deg, #d4fc79, #96e6a1)', font: `'Pacifico', cursive` },
  { name: 'Margarita', icon: 'üçã', gradient: 'linear-gradient(135deg, #fceabb, #f8b500)', font: `'Bebas Neue', sans-serif` },
  { name: 'Martini', icon: 'üç∏', gradient: 'linear-gradient(135deg, #e0c3fc, #8ec5fc)', font: `'Playfair Display', serif` },
  { name: 'Manhattan', icon: 'ü•É', gradient: 'linear-gradient(135deg, #fdfcfb, #e2d1c3)', font: `'Lora', serif` },
  { name: 'Cosmopolitan', icon: 'üç∑', gradient: 'linear-gradient(135deg, #ff9a9e, #fecfef)', font: `'Indie Flower', cursive` },
  { name: 'Mai Tai', icon: 'üçç', gradient: 'linear-gradient(135deg, #f6d365, #fda085)', font: `'Fredoka', sans-serif` },
  { name: 'Pina Colada', icon: 'ü••', gradient: 'linear-gradient(135deg, #d9afd9, #97d9e1)', font: `'Montserrat', sans-serif` },
  { name: 'Old Fashioned', icon: 'üßä', gradient: 'linear-gradient(135deg, #c2e9fb, #a1c4fd)', font: `'Merriweather', serif` }
];





  logo = 'CocktailForYou'; // Or use your logo image URL

  constructor(private router: Router) {}

  // Navigate to cocktail page when card is clicked
  goToCocktail(name: string) {
    this.router.navigate(['/cocktail', name]);
  }
}
<div class="home-container">
  <div class="card-grid">
    <div
      class="drink-card"
      *ngFor="let cocktail of cocktails"
      [ngStyle]="{
        'background-image': cocktail.gradient,
        'font-family': cocktail.font
      }"
      (click)="goToCocktail(cocktail.name)"
    >
      <div class="overlay-title">{{ cocktail.name }}</div>
    </div>
  </div>
</div>
