import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import {
  COCKTAIL_NAMES,
  COMMON_COCKTAIL_ICON,
  COMMON_COCKTAIL_GRADIENT,
} from 'src/app/core/data/cocktail.data';
import { PageTitleService } from 'src/app/core/services/page-title.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements OnInit {
  cocktailNames = COCKTAIL_NAMES;
  icon = COMMON_COCKTAIL_ICON;
  gradient = COMMON_COCKTAIL_GRADIENT;


  constructor(private router: Router, private pageTitle: PageTitleService) {}
  ngOnInit(): void {
    this.pageTitle.setHomeTitle();
  }

  goToCocktail(name: string) {
    this.router.navigate(['/cocktail', name]);
  }
}
<div class="home-container">
  <div class="card-grid">
    <div
      class="drink-card"
      *ngFor="let name of cocktailNames"
      [ngStyle]="{ 'background-image': gradient }"
      (click)="goToCocktail(name)"
    >
      <div class="drink-content">
        <div class="drink-title" tabindex="0">{{ name }}</div>
        <div class="drink-icon">
          <img [src]="icon" alt="{{ name }} icon" />
        </div>
      </div>
    </div>
  </div>
</div>
