import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  // Static cocktails for the home page cards
cocktails = [
  {
    name: 'Espresso Martini',
    gradient: 'linear-gradient(135deg, #a1887f, #5d4037)',
    font: `'Poppins', sans-serif`
  },
  {
    name: 'French 75',
    gradient: 'linear-gradient(135deg, #bcaaa4, #6d4c41)',
    font: `'Poppins', sans-serif`
  },
  {
    name: 'Last Word',
    gradient: 'linear-gradient(135deg, #d7ccc8, #8d6e63)',
    font: `'Poppins', sans-serif`
  }
];






  logo = 'CocktailForYou'; // Or use your logo image URL

  constructor(private router: Router) {}

  // Navigate to cocktail page when card is clicked
  goToCocktail(name: string) {
    this.router.navigate(['/cocktail', name]);
  }
}
<div class="home-container">
  <div class="card-grid">
    <div
      class="drink-card"
      *ngFor="let cocktail of cocktails"
      [ngStyle]="{ 'background-image': cocktail.gradient }"
      (click)="goToCocktail(cocktail.name)"
    >
      <div class="drink-content">
        <div class="drink-title" tabindex="0">{{ cocktail.name }}</div>
        <div class="drink-icon">
          <img [src]="cocktail.icon" alt="{{ cocktail.name }} icon" />
        </div>
      </div>
    </div>
  </div>
</div>
