import { Component, OnInit } from '@angular/core';
import { CocktailService } from 'src/app/core/services/cocktail.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-cocktails',
  templateUrl: './cocktails.component.html',
  styleUrls: ['./cocktails.component.scss'],
})
export class CocktailsComponent implements OnInit {
  cocktails: any[] = [];
  searchText: string = '';
  cocktailName: string = '';
  sortAsc = true;
  showSearch = false;

  filters = {
    alcoholic: false,
    nonAlcoholic: false,
  };

  constructor(
    private cocktailService: CocktailService,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      this.cocktailName = params['name'];
      if (this.cocktailName) {
        this.getCocktails(this.cocktailName);
      }
    });
  }

  getCocktails(name: string) {
    this.cocktailService.getCocktailByName(name).subscribe((res: any) => {
      this.cocktails = res.drinks || [];
    });
  }

  toggleFilter(key: 'alcoholic' | 'nonAlcoholic', event: any) {
    this.filters = {
      ...this.filters,
      [key]: event.target.checked,
    };
  }

  toggleSearch() {
    this.showSearch = !this.showSearch;
    if (!this.showSearch) {
      this.searchText = '';
    }
  }

  toggleSort() {
    this.sortAsc = !this.sortAsc;
  }

  clearFilters() {
    this.filters = {
      alcoholic: false,
      nonAlcoholic: false,
    };
    this.searchText = '';
  }

  // Optional: Capitalize cocktailName for breadcrumb display
  get formattedCocktailName(): string {
    return this.cocktailName
      .split(' ')
      .map(word => word.charAt(0).toUpperCase() + word.slice(1))
      .join(' ');
  }
}
<!-- Breadcrumb -->
<div class="breadcrumb">
  <a routerLink="/">Home</a> &gt; <span>{{ formattedCocktailName }}</span>
</div>

<!-- Main Page Layout -->
<div class="cocktail-page">
  <!-- Filter Sidebar -->
  <aside class="filter-panel">
    <h3>Filter</h3>
    <label>
      <input type="checkbox" [checked]="filters.alcoholic" (change)="toggleFilter('alcoholic', $event)" />
      Alcoholic
    </label>
    <label>
      <input type="checkbox" [checked]="filters.nonAlcoholic" (change)="toggleFilter('nonAlcoholic', $event)" />
      Non-Alcoholic
    </label>
  </aside>

  <!-- Main Content -->
  <section class="main-section">
    <!-- Top Controls Row -->
    <div class="top-row">
      <!-- Sort Section -->
      <div class="sort" (click)="toggleSort()">
        Sort by: <span>Name</span>
        <span class="material-icons sort-icon">
          {{ sortAsc ? 'arrow_upward' : 'arrow_downward' }}
        </span>
      </div>

      <!-- Search Section -->
      <div class="search-box">
        <ng-container *ngIf="showSearch; else collapsedSearch">
          <span class="cancel" (click)="toggleSearch()">Cancel</span>
          <input
            type="text"
            [(ngModel)]="searchText"
            placeholder="Search cocktails"
          />
          <span class="material-icons icon">search</span>
          <span class="clear-filter" (click)="clearFilters()">Clear Filters</span>
        </ng-container>
        <ng-template #collapsedSearch>
          <span class="search-label" (click)="toggleSearch()">Search</span>
          <span class="material-icons search-icon" (click)="toggleSearch()">search</span>
        </ng-template>
      </div>
    </div>

    <!-- Cocktail Cards Grid -->
    <div class="cocktail-grid">
      <a
        class="card"
        *ngFor="let cocktail of cocktails | cocktailFilter: searchText : filters : sortAsc"
        [routerLink]="['/ingredients', cocktail.idDrink]"
      >
        <div class="card-image">
          <img [src]="cocktail.strDrinkThumb" alt="{{ cocktail.strDrink }}" />
        </div>
        <div class="card-footer">
          <h4>{{ cocktail.strDrink }}</h4>
        </div>
      </a>
    </div>

    <!-- No Results Message -->
    <p *ngIf="(cocktails | cocktailFilter: searchText : filters : sortAsc).length === 0" class="no-results">
      No cocktails found.
    </p>
  </section>
</div>
