import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { CocktailService } from 'src/app/core/services/cocktail.service';

@Component({
  selector: 'app-cocktails',
  templateUrl: './cocktails.component.html',
  styleUrls: ['./cocktails.component.scss'],
})
export class CocktailsComponent implements OnInit {
  cocktails: any[] = [];
  filteredCocktails: any[] = [];
  searchTerm = '';
  searchExpanded = false;
  sortOption = 'name-asc';
  activeFilters: string[] = [];

  constructor(private route: ActivatedRoute, private cocktailService: CocktailService) {}

  ngOnInit(): void {
    const name = this.route.snapshot.paramMap.get('name') || '';
    this.cocktailService.getCocktailByName(name).subscribe((res: any) => {
      this.cocktails = res.drinks || [];
      this.filteredCocktails = [...this.cocktails];
    });
  }

  toggleSearch() {
    this.searchExpanded = !this.searchExpanded;
    if (!this.searchExpanded) this.searchTerm = '';
  }

  searchCocktails() {
    const term = this.searchTerm.toLowerCase();
    this.filteredCocktails = this.cocktails.filter((d) =>
      d.strDrink.toLowerCase().includes(term)
    );
  }

  sortCocktails() {
    const sorted = [...this.filteredCocktails];
    sorted.sort((a, b) => {
      if (this.sortOption === 'name-asc') return a.strDrink.localeCompare(b.strDrink);
      else return b.strDrink.localeCompare(a.strDrink);
    });
    this.filteredCocktails = sorted;
  }

  toggleFilter(type: string) {
    const index = this.activeFilters.indexOf(type);
    if (index >= 0) this.activeFilters.splice(index, 1);
    else this.activeFilters.push(type);

    this.applyFilters();
  }

  applyFilters() {
    this.filteredCocktails = this.cocktails.filter((d) => {
      if (this.activeFilters.length === 0) return true;
      return this.activeFilters.includes(d.strAlcoholic.replace(/\s/g, '_'));
    });
  }
}
<div class="cocktails-page">
  <!-- Sidebar filter -->
  <aside class="filter-panel">
    <h3>Filter</h3>
    <label>
      <input type="checkbox" (change)="toggleFilter('Alcoholic')" /> Alcoholic
    </label>
    <label>
      <input type="checkbox" (change)="toggleFilter('Non_Alcoholic')" /> Non-Alcoholic
    </label>
  </aside>

  <!-- Main content -->
  <section class="content-panel">
    <div class="toolbar">
      <div class="sort-dropdown">
        <label>Sort by: </label>
        <select [(ngModel)]="sortOption" (change)="sortCocktails()">
          <option value="name-asc">Name ↑</option>
          <option value="name-desc">Name ↓</option>
        </select>
      </div>

      <div class="search-box" [class.expanded]="searchExpanded">
        <input
          *ngIf="searchExpanded"
          type="text"
          [(ngModel)]="searchTerm"
          placeholder="Search..."
          (keyup.enter)="searchCocktails()"
        />
        <button (click)="toggleSearch()">
          <i class="material-icons">search</i>
        </button>
      </div>
    </div>

    <!-- Cocktail cards -->
    <div class="cocktail-grid">
      <div class="card" *ngFor="let drink of filteredCocktails">
        <img [src]="drink.strDrinkThumb" alt="cocktail" />
        <h4>{{ drink.strDrink }}</h4>
        <p>{{ drink.strAlcoholic }} • {{ drink.strCategory }}</p>
      </div>
    </div>
  </section>
</div>
