import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'searchFilter',
  pure: true
})
export class SearchFilterPipe implements PipeTransform {
  transform(
    items: any[],
    searchText: string,
    field: string,
    filters: { alcoholic: boolean; nonAlcoholic: boolean },
    sortAsc: boolean = true
  ): any[] {
    if (!items) return [];

    // 1. Apply search
    if (searchText) {
      const lowerSearch = searchText.toLowerCase();
      items = items.filter(item =>
        item[field]?.toLowerCase().includes(lowerSearch)
      );
    }

    // 2. Apply checkbox filters (normalize and match)
    items = items.filter(item => {
      const type = (item.strAlcoholic || '').trim().toLowerCase(); // e.g. "alcoholic"

      const showAlcoholic = filters.alcoholic && type === 'alcoholic';
      const showNonAlcoholic = filters.nonAlcoholic && type === 'non alcoholic';

      return (
        (!filters.alcoholic && !filters.nonAlcoholic) || showAlcoholic || showNonAlcoholic
      );
    });

    // 3. Sort by name (default: ascending)
    return items.sort((a, b) =>
      sortAsc
        ? a[field].localeCompare(b[field])
        : b[field].localeCompare(a[field])
    );
  }
}
