// src/app/core/pipes/search-filter.pipe.ts

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'searchFilter'
})
export class SearchFilterPipe implements PipeTransform {
  transform(
    items: any[],
    searchText: string,
    field: string,
    filters: { alcoholic: boolean; nonAlcoholic: boolean },
    sortAsc: boolean = true
  ): any[] {
    if (!items) return [];

    // Apply search
    if (searchText) {
      const lowerSearch = searchText.toLowerCase();
      items = items.filter(item =>
        item[field]?.toLowerCase().includes(lowerSearch)
      );
    }

    // Apply checkbox filter
    items = items.filter(item => {
      return (
        (!filters.alcoholic && !filters.nonAlcoholic) ||
        (filters.alcoholic && item.strAlcoholic === 'Alcoholic') ||
        (filters.nonAlcoholic && item.strAlcoholic === 'Non alcoholic')
      );
    });

    // Sort result
    return items.sort((a, b) =>
      sortAsc
        ? a[field].localeCompare(b[field])
        : b[field].localeCompare(a[field])
    );
  }
}
