import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { CocktailService } from 'src/app/core/services/cocktail.service';

@Component({
  selector: 'app-ingredients',
  templateUrl: './ingredients.component.html',
  styleUrls: ['./ingredients.component.scss']
})
export class IngredientsComponent implements OnInit {
  cocktailName: string | null = null;
  cocktailGroup: string | null = null;
  drink: any;

  constructor(private route: ActivatedRoute, private cocktailService: CocktailService) {}

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      const id = params.get('id');
      this.cocktailName = params.get('name');  // Get cocktail name from the route

      if (id) {
        this.cocktailService.getDrinkById(id).subscribe((res: any) => {
          this.drink = res.drinks ? res.drinks[0] : null;
        });
      }
    });

this.route.queryParamMap.subscribe(params => {
  this.cocktailGroup = params.get('group');
});



  }

  getIngredients(): { ingredient: string; measure: string }[] {
    if (!this.drink) return [];
    const ingredients = [];
    for (let i = 1; i <= 15; i++) {
      const ingredient = this.drink[`strIngredient${i}`];
      const measure = this.drink[`strMeasure${i}`];
      if (ingredient) {
        ingredients.push({ ingredient, measure });
      }
    }
    return ingredients;
  }

  getIngredientImage(name: string): string {
    return `https://www.thecocktaildb.com/images/ingredients/${name}-Small.png`;
  }

  // Optional: Format the name as required
  get formattedCocktailName(): string {
    return this.cocktailName?.split(' ').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ') || '';
  }
}
<!-- Breadcrumb -->
<div class="breadcrumb">
  <a routerLink="/">Home</a>
  <ng-container *ngIf="cocktailGroup">
    &gt; <a [routerLink]="['/cocktail', cocktailGroup]">{{ cocktailGroup }}</a>
  </ng-container>
  <ng-container *ngIf="drink">
    &gt; <span class="breadcrumb-current">{{ drink.strDrink }}</span>
  </ng-container>
</div>

<!-- Ingredient Layout -->
<div *ngIf="drink" class="ingredient-layout">
  <div class="matched-row">
    <!-- Left Side -->
    <div class="matched-left">
      <h2>{{ drink.strDrink }}</h2>
      <img [src]="drink.strDrinkThumb" [alt]="drink.strDrink" class="drink-img" />
    </div>

    <!-- Divider -->
    <div class="matched-divider"></div>

    <!-- Right Side -->
    <div class="matched-right">
      <h3>Ingredients</h3>
      <div class="ingredient-flex">
        <div class="ingredient-item" *ngFor="let item of getIngredients()">
          <img [src]="getIngredientImage(item.ingredient)" [alt]="item.ingredient" />
          <span>{{ item.ingredient }}</span>
        </div>
      </div>
    </div>
  </div>
</div>
