import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { CocktailService } from 'src/app/core/services/cocktail.service';

@Component({
  selector: 'app-ingredients',
  templateUrl: './ingredients.component.html',
  styleUrls: ['./ingredients.component.scss'],
})
export class IngredientsComponent implements OnInit {
  drink: any;

  constructor(
    private route: ActivatedRoute,
    private cocktailService: CocktailService
  ) {}

  ngOnInit(): void {
    const id = this.route.snapshot.paramMap.get('id') || '';
    this.cocktailService.getDrinkById(id).subscribe((res: any) => {
      this.drink = res.drinks?.[0] || null;
    });
  }

  getIngredients(): { ingredient: string; measure: string }[] {
    if (!this.drink) return [];

    const uniqueIngredients = new Set<string>();
    const ingredients: { ingredient: string; measure: string }[] = [];

    for (let i = 1; i <= 15; i++) {
      const ingr = this.drink[`strIngredient${i}`];
      const measure = this.drink[`strMeasure${i}`] || '';

      if (
        ingr &&
        !uniqueIngredients.has(ingr.trim().toLowerCase())
      ) {
        ingredients.push({
          ingredient: ingr.trim(),
          measure: measure.trim(),
        });
        uniqueIngredients.add(ingr.trim().toLowerCase());
      }
    }

    return ingredients;
  }

  getIngredientImage(ingredient: string): string {
    return `https://www.thecocktaildb.com/images/ingredients/${encodeURIComponent(
      ingredient
    )}.png`;
  }
}
<div class="ingredient-matched-container">
  <div class="matched-row">
    <div class="matched-left">
      <h2>{{ drink.strDrink }}</h2>
      <img [src]="drink.strDrinkThumb" [alt]="drink.strDrink" class="matched-drink-image" />
    </div>

    <!-- ✅ Divider -->
    <div class="vertical-divider"></div>

    <div class="matched-right">
      <h3>Ingredients</h3>
      <div class="matched-ingredients-grid">
        <div class="ingredient-card" *ngFor="let item of getIngredients()">
          <img [src]="getIngredientImage(item.ingredient)" alt="{{ item.ingredient }}" />
          <div class="caption">{{ item.ingredient }}</div>
        </div>
      </div>
    </div>
  </div>
</div>
