import { Component, OnInit } from '@angular/core';
import { CocktailService } from 'src/app/core/services/cocktail.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-cocktails',
  templateUrl: './cocktails.component.html',
  styleUrls: ['./cocktails.component.scss'],
})
export class CocktailsComponent implements OnInit {
  cocktails: any[] = [];
  filteredCocktails: any[] = [];
  searchText: string = '';
  cocktailName: string = '';
  sortAsc = true;
  showSearch = false; // renamed for consistency
  filters = {
    alcoholic: false,
    nonAlcoholic: false,
  };

  constructor(
    private cocktailService: CocktailService,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      this.cocktailName = params['name'];
      if (this.cocktailName) {
        this.getCocktails(this.cocktailName);
      }
    });
  }

  getCocktails(name: string) {
    this.cocktailService.getCocktailByName(name).subscribe((res: any) => {
      this.cocktails = res.drinks || [];
      this.applyFilters(); // always apply filters initially
    });
  }

  toggleSearch() {
    this.showSearch = !this.showSearch;
    if (!this.showSearch) {
      this.searchText = '';
    }
    this.applyFilters();
  }

  onSearchChange() {
    this.applyFilters();
  }

  toggleSort() {
    this.sortAsc = !this.sortAsc;
    this.applyFilters(); // apply sorting again
  }

  applyFilters() {
    this.filteredCocktails = this.cocktails
      .filter((drink) => {
        const matchesSearch = drink.strDrink
          .toLowerCase()
          .includes(this.searchText.toLowerCase());

        const matchesFilter =
          (!this.filters.alcoholic && !this.filters.nonAlcoholic) ||
          (this.filters.alcoholic && drink.strAlcoholic === 'Alcoholic') ||
          (this.filters.nonAlcoholic && drink.strAlcoholic === 'Non alcoholic');

        return matchesSearch && matchesFilter;
      })
      .sort((a, b) =>
        this.sortAsc
          ? a.strDrink.localeCompare(b.strDrink)
          : b.strDrink.localeCompare(a.strDrink)
      );
  }
}
<div class="cocktail-page">
  <!-- Sidebar Filter -->
  <aside class="filter-panel">
    <h3>FILTER</h3>
    <label>
      <input type="checkbox" [(ngModel)]="filters.alcoholic" (change)="applyFilters()" />
      Alcoholic
    </label>
    <label>
      <input type="checkbox" [(ngModel)]="filters.nonAlcoholic" (change)="applyFilters()" />
      Non-Alcoholic
    </label>
  </aside>

  <!-- Divider -->
  <div class="vertical-divider"></div>

  <!-- Main Content -->
  <section class="content-area">
    <div class="controls">
      <div class="sort" (click)="toggleSort()">
        Sort by: <span>Name</span>
        <span class="material-icons sort-icon">{{ sortAsc ? 'arrow_upward' : 'arrow_downward' }}</span>
      </div>

      <!-- Search -->
      <div class="search-container">
        <ng-container *ngIf="showSearch; else collapsed">
          <span class="cancel" (click)="toggleSearch()">Cancel</span>
          <div class="search-box">
            <input
              type="text"
              [(ngModel)]="searchText"
              (input)="onSearchChange()"
              placeholder="Search cocktails"
            />
            <span class="material-icons icon">search</span>
          </div>
        </ng-container>
        <ng-template #collapsed>
          <span class="search-label" (click)="toggleSearch()">Search</span>
          <span class="material-icons search-icon" (click)="toggleSearch()">search</span>
        </ng-template>
      </div>
    </div>

    <!-- Cocktail Grid -->
    <div class="cocktail-grid">
      <a
        class="card"
        *ngFor="let cocktail of filteredCocktails"
        [routerLink]="['/ingredients', cocktail.idDrink]"
      >
        <div class="card-image">
          <img [src]="cocktail.strDrinkThumb" alt="{{ cocktail.strDrink }}" />
        </div>
        <div class="card-details">
          <h4>{{ cocktail.strDrink }}</h4>
          <p>{{ cocktail.strAlcoholic }} | {{ cocktail.strCategory }}</p>
          <div class="see-details">See Ingredients →</div>
        </div>
      </a>
    </div>

    <p *ngIf="filteredCocktails.length === 0" class="no-results">
      No cocktails found for "{{ searchText }}"
    </p>
  </section>
</div>
