import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  // Static cocktails for the home page cards





  logo = 'CocktailForYou'; // Or use your logo image URL

  constructor(private router: Router) {}

  // Navigate to cocktail page when card is clicked
  goToCocktail(name: string) {
    this.router.navigate(['/cocktail', name]);
  }
}
<div class="home-container">
  <div class="card-grid">
    <div
      class="drink-card"
      *ngFor="let cocktail of cocktails"
      [ngStyle]="{
        'background-color': cocktail.color,
        'font-family': cocktail.font
      }"
      (click)="goToCocktail(cocktail.name)"
    >
      <div class="drink-icon">{{ cocktail.icon }}</div>
      <div class="drink-name">{{ cocktail.name }}</div>
    </div>
  </div>
</div>
