import { Component, OnInit } from '@angular/core';
import { CocktailService } from 'src/app/core/services/cocktail.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-cocktails',
  templateUrl: './cocktails.component.html',
  styleUrls: ['./cocktails.component.scss'],
})
export class CocktailsComponent implements OnInit {
  cocktails: any[] = [];
  filteredCocktails: any[] = [];
  searchText: string = '';
  cocktailName: string = '';
  sortAsc = true;
  showSearchInput = false;
  filters = {
    alcoholic: false,
    nonAlcoholic: false,
  };

  constructor(private cocktailService: CocktailService, private route: ActivatedRoute) {}

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      this.cocktailName = params['name'];
      if (this.cocktailName) {
        this.getCocktails(this.cocktailName);
      }
    });
  }

  getCocktails(name: string) {
    this.cocktailService.getCocktailByName(name).subscribe((res: any) => {
      this.cocktails = res.drinks || [];
      this.filteredCocktails = [...this.cocktails];
    });
  }

  toggleSearch() {
    this.showSearchInput = !this.showSearchInput;
    if (!this.showSearchInput) this.searchText = '';
    this.applyFilters();
  }

  onSearchChange() {
    this.applyFilters();
  }

  toggleSort() {
    this.sortAsc = !this.sortAsc;
    this.filteredCocktails.sort((a, b) => {
      return this.sortAsc
        ? a.strDrink.localeCompare(b.strDrink)
        : b.strDrink.localeCompare(a.strDrink);
    });
  }

  applyFilters() {
    this.filteredCocktails = this.cocktails.filter((drink) => {
      const matchesSearch = drink.strDrink
        .toLowerCase()
        .includes(this.searchText.toLowerCase());

      const matchesFilter =
        (!this.filters.alcoholic && !this.filters.nonAlcoholic) ||
        (this.filters.alcoholic && drink.strAlcoholic === 'Alcoholic') ||
        (this.filters.nonAlcoholic && drink.strAlcoholic === 'Non alcoholic');

      return matchesSearch && matchesFilter;
    });
  }
}
<div class="cocktail-page">
  <!-- Sidebar Filter -->
  <aside class="filter-panel">
    <h3>FILTER</h3>
    <label>
      <input type="checkbox" [(ngModel)]="filters.alcoholic" (change)="applyFilters()" />
      Alcoholic
    </label>
    <label>
      <input type="checkbox" [(ngModel)]="filters.nonAlcoholic" (change)="applyFilters()" />
      Non-Alcoholic
    </label>
  </aside>

  <!-- Main Content -->
  <section class="content-area">
    <!-- Controls: Sort + Search -->
    <div class="controls">
      <!-- Sort -->
      <div class="sort" (click)="toggleSort()">
        Sort by: <span>Name</span>
        <span class="material-icons sort-icon">
          {{ sortAsc ? 'arrow_upward' : 'arrow_downward' }}
        </span>
      </div>

      <!-- Search (fixed width, no shift) -->
      <div class="search-container">
        <span *ngIf="showSearchInput" class="cancel" (click)="toggleSearch()">Cancel</span>

        <div class="search-box">
          <input
            *ngIf="showSearchInput"
            type="text"
            [(ngModel)]="searchText"
            (input)="onSearchChange()"
            placeholder="Search cocktails"
          />
          <span
            class="material-icons search-icon"
            (click)="toggleSearch()"
            title="Search"
          >
            search
          </span>
        </div>
      </div>
    </div>

    <!-- Cocktails Grid -->
    <div class="cocktail-grid">
      <div class="card" *ngFor="let cocktail of filteredCocktails">
        <img [src]="cocktail.strDrinkThumb" alt="{{ cocktail.strDrink }}" />
        <h4>{{ cocktail.strDrink }}</h4>
        <p>{{ cocktail.strAlcoholic }} | {{ cocktail.strCategory }}</p>
      </div>
    </div>

    <p *ngIf="filteredCocktails.length === 0" class="no-results">
      No cocktails found for "{{ searchText }}"
    </p>
  </section>
</div>
