import { Component, OnInit } from '@angular/core';
import { CocktailService } from 'src/app/core/services/cocktail.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-cocktails',
  templateUrl: './cocktails.component.html',
  styleUrls: ['./cocktails.component.scss'],
})
export class CocktailsComponent implements OnInit {
  cocktails: any[] = [];
  filteredCocktails: any[] = [];
  searchText: string = '';
  cocktailName: string = '';
  sortAsc = true;
  showSearch = false; // renamed for consistency
  filters = {
    alcoholic: false,
    nonAlcoholic: false,
  };

  constructor(
    private cocktailService: CocktailService,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      this.cocktailName = params['name'];
      if (this.cocktailName) {
        this.getCocktails(this.cocktailName);
      }
    });
  }

  getCocktails(name: string) {
    this.cocktailService.getCocktailByName(name).subscribe((res: any) => {
      this.cocktails = res.drinks || [];
      this.applyFilters(); // always apply filters initially
    });
  }

  toggleSearch() {
    this.showSearch = !this.showSearch;
    if (!this.showSearch) {
      this.searchText = '';
    }
    this.applyFilters();
  }

  onSearchChange() {
    this.applyFilters();
  }

  toggleSort() {
    this.sortAsc = !this.sortAsc;
    this.applyFilters(); // apply sorting again
  }

  applyFilters() {
    this.filteredCocktails = this.cocktails
      .filter((drink) => {
        const matchesSearch = drink.strDrink
          .toLowerCase()
          .includes(this.searchText.toLowerCase());

        const matchesFilter =
          (!this.filters.alcoholic && !this.filters.nonAlcoholic) ||
          (this.filters.alcoholic && drink.strAlcoholic === 'Alcoholic') ||
          (this.filters.nonAlcoholic && drink.strAlcoholic === 'Non alcoholic');

        return matchesSearch && matchesFilter;
      })
      .sort((a, b) =>
        this.sortAsc
          ? a.strDrink.localeCompare(b.strDrink)
          : b.strDrink.localeCompare(a.strDrink)
      );
  }
}
