import { Component, OnInit } from '@angular/core';
import { CocktailService } from 'src/app/core/services/cocktail.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-cocktails',
  templateUrl: './cocktails.component.html',
  styleUrls: ['./cocktails.component.scss'],
})
export class CocktailsComponent implements OnInit {
  cocktails: any[] = [];
  searchText: string = '';
  cocktailName: string = '';
  sortAsc = true;
  showSearch = false;

  filters = {
    alcoholic: false,
    nonAlcoholic: false,
  };

  constructor(
    private cocktailService: CocktailService,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      this.cocktailName = params['name'];
      if (this.cocktailName) {
        this.getCocktails(this.cocktailName);
      }
    });
  }

  getCocktails(name: string) {
    this.cocktailService.getCocktailByName(name).subscribe((res: any) => {
      this.cocktails = res.drinks || [];
    });
  }

  toggleAlcoholic(event: any) {
    this.filters = { ...this.filters, alcoholic: event.target.checked };
  }

  toggleSearch() {
    this.showSearch = !this.showSearch;
    if (!this.showSearch) {
      this.searchText = '';
    }
  }

  toggleSort() {
    this.sortAsc = !this.sortAsc;
  }
}
<div class="cocktail-page">
  <!-- Sidebar Filter -->
  <aside class="filter-panel">
    <h3>FILTER</h3>
    <label>
    <input type="checkbox" [checked]="filters.alcoholic" (change)="toggleAlcoholic($event)" />

      Alcoholic
    </label>
    <label>
       <input type="checkbox" [checked]="filters.nonalcoholic" (change)="toggleAlcoholic($event)" />
      Non-Alcoholic
    </label>
  </aside>

  <!-- Divider -->
  <div class="vertical-divider"></div>

  <!-- Main Content -->
  <section class="content-area">
    <!-- Controls: Sort + Search -->
    <div class="controls">
      <!-- Sort -->
      <div class="sort" (click)="toggleSort()">
        Sort by: <span>Name</span>
        <span class="material-icons sort-icon">
          {{ sortAsc ? 'arrow_upward' : 'arrow_downward' }}
        </span>
      </div>

      <!-- Search -->
      <div class="search-container">
        <ng-container *ngIf="showSearch; else collapsed">
          <span class="cancel" (click)="toggleSearch()">Cancel</span>
          <div class="search-box">
            <input
              type="text"
              [(ngModel)]="searchText"
              placeholder="Search cocktails"
            />
            <span class="material-icons icon">search</span>
          </div>
        </ng-container>
        <ng-template #collapsed>
          <span class="search-label" (click)="toggleSearch()">Search</span>
          <span class="material-icons search-icon" (click)="toggleSearch()">search</span>
        </ng-template>
      </div>
    </div>

    <!-- Cocktail Cards -->
    <div class="cocktail-grid">
      <a
        class="card"
        *ngFor="let cocktail of cocktails | cocktailFilter: searchText : filters : sortAsc"
        [routerLink]="['/ingredients', cocktail.idDrink]"
      >
        <div class="card-image">
          <img [src]="cocktail.strDrinkThumb" alt="{{ cocktail.strDrink }}" />
          <div class="card-overlay">
            <button class="see-recipe">See Ingredients</button>
          </div>
        </div>
        <div class="card-footer">
          <h4>{{ cocktail.strDrink }}</h4>
          <p>{{ cocktail.strAlcoholic }} | {{ cocktail.strCategory }}</p>
        </div>
      </a>
    </div>

    <!-- No Results -->
    <p
      *ngIf="(cocktails | cocktailFilter: searchText : filters : sortAsc).length === 0"
      class="no-results"
    >
      No cocktails found for "{{ searchText }}"
    </p>
  </section>
</div>
