import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  // Static cocktails for the home page cards
  cocktails = [
  { name: 'Mojito', color: '#E6F7F2' },
  { name: 'Margarita', color: '#FFF2E5' },
  { name: 'Martini', color: '#F3E6FF' },
  { name: 'Manhattan', color: '#FCEAE8' },
  { name: 'Cosmopolitan', color: '#E6F0FF' },
  { name: 'Mai Tai', color: '#FFF9E6' },
  { name: 'Pina Colada', color: '#F0FFF4' },
  { name: 'Old Fashioned', color: '#F5F5F5' }
];

  logo = 'CocktailForYou'; // Or use your logo image URL

  constructor(private router: Router) {}

  // Navigate to cocktail page when card is clicked
  goToCocktail(name: string) {
    this.router.navigate(['/cocktail', name]);
  }
}
<div class="home-container">
  <h1>CocktailForYou</h1>

  <div class="card-grid">
    <div
      class="drink-card"
      *ngFor="let cocktail of cocktails"
      [ngStyle]="{ 'background-color': cocktail.color }"
      (click)="goToCocktail(cocktail.name)"
    >
      <span>{{ cocktail.name }}</span>
    </div>
  </div>
</div>
