import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  // Static cocktails for the home page cards
  cocktails = ['Mojito', 'Margarita', 'Martini', 'Manhattan', 'Cosmopolitan','Mai Tai', 'Pina Colada','Old Fashioned' ];
  logo = 'CocktailForYou'; // Or use your logo image URL

  constructor(private router: Router) {}

  // Navigate to cocktail page when card is clicked
  goToCocktail(name: string) {
    this.router.navigate(['/cocktail', name]);
  }
}
<div class="home-container">
  <h1>
    <span class="logo-part">COCKTAIL</span>
    <img src="assets/logo/cocktail-glass.png" alt="cocktail glass" class="inline-logo" />
    <span class="logo-part2">FOR YOU</span>
  </h1>

  <div class="cocktail-grid">
    <div 
      class="cocktail-card"
      *ngFor="let cocktail of cocktails" 
      (click)="goToCocktail(cocktail)"
      tabindex="0" 
      (keyup.enter)="goToCocktail(cocktail)">
      <img
        src="assets/logo/cocktail-glass.png"
        alt="Cocktail glass icon"
        class="card-icon"
      />
      <div class="cocktail-title">{{ cocktail }}</div>
    </div>
  </div>
</div>
