import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  // Static cocktails for the home page cards
 cocktails = [
  { name: 'Magical Cocktail', color: '#f3e8ff' },   // Light lavender
  { name: 'Rimberio', color: '#f9f9f7' },           // Off-white
  { name: 'Fresca', color: '#c6e8e6' },             // Pale teal
  { name: 'Borcelli', color: '#f9f6f2' },           // Ivory
  { name: 'Fresh Drink', color: '#fff7f7' },        // Soft pink
  { name: 'Harumi', color: '#f7f3ef' },             // Cream white
  { name: 'Bevande', color: '#006b6a' },            // Deep teal
  { name: 'Green Lime', color: '#fbfdf4' }          // Light lime
];


  logo = 'CocktailForYou'; // Or use your logo image URL

  constructor(private router: Router) {}

  // Navigate to cocktail page when card is clicked
  goToCocktail(name: string) {
    this.router.navigate(['/cocktail', name]);
  }
}
<div class="home-container">
  <div class="card-grid">
    <div
      class="drink-card"
      *ngFor="let cocktail of cocktails"
      [ngStyle]="{ 'background-color': cocktail.color }"
      (click)="goToCocktail(cocktail.name)"
    >
      <div class="drink-name">{{ cocktail.name }}</div>
    </div>
  </div>
</div>
