import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { CocktailService } from 'src/app/core/services/cocktail.service';

@Component({
  selector: 'app-cocktails',
  templateUrl: './cocktails.component.html',
  styleUrls: ['./cocktails.component.scss']
})
export class CocktailsComponent implements OnInit {
  allDrinks: any[] = [];
  filteredDrinks: any[] = [];
  cocktailName = '';
  searchQuery = '';
  sortBy = 'asc';

  filters = {
    alcoholic: false,
    nonAlcoholic: false
  };

  constructor(private cocktailService: CocktailService, private route: ActivatedRoute) {}

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      this.cocktailName = params['name'];
      this.cocktailService.getCocktailByName(this.cocktailName).subscribe((res: any) => {
        this.allDrinks = res.drinks || [];
        this.filteredDrinks = [...this.allDrinks];
      });
    });
  }

  applyFilters(): void {
    this.filteredDrinks = this.allDrinks.filter(drink => {
      const matchesSearch = this.searchQuery === '' || drink.strDrink.toLowerCase().includes(this.searchQuery.toLowerCase());
      const isAlcoholic = drink.strAlcoholic.toLowerCase().includes('alcoholic');
      const matchesAlcohol =
        (!this.filters.alcoholic && !this.filters.nonAlcoholic) ||
        (this.filters.alcoholic && isAlcoholic) ||
        (this.filters.nonAlcoholic && !isAlcoholic);
      return matchesSearch && matchesAlcohol;
    });

    this.filteredDrinks.sort((a, b) =>
      this.sortBy === 'asc'
        ? a.strDrink.localeCompare(b.strDrink)
        : b.strDrink.localeCompare(a.strDrink)
    );
  }
}
<div class="cocktails-container">
  <!-- Left Sidebar Filters -->
  <aside class="sidebar">
    <h3>Filter By</h3>
    <label>
      <input type="checkbox" [(ngModel)]="filters.alcoholic" (change)="applyFilters()" />
      Alcoholic
    </label>
    <label>
      <input type="checkbox" [(ngModel)]="filters.nonAlcoholic" (change)="applyFilters()" />
      Non-Alcoholic
    </label>
  </aside>

  <!-- Main Content -->
  <section class="content">
    <div class="top-controls">
      <div class="search-box">
        <input
          type="text"
          placeholder="Search cocktails..."
          [(ngModel)]="searchQuery"
          (input)="applyFilters()" />
        <i class="fa fa-search"></i>
      </div>

      <select [(ngModel)]="sortBy" (change)="applyFilters()">
        <option value="asc">Name ↑</option>
        <option value="desc">Name ↓</option>
      </select>
    </div>

    <div class="card-grid">
      <div class="cocktail-card" *ngFor="let drink of filteredDrinks">
        <img [src]="drink.strDrinkThumb" [alt]="drink.strDrink" />
        <h4>{{ drink.strDrink }}</h4>
        <p>{{ drink.strAlcoholic }}</p>
      </div>

      <p *ngIf="filteredDrinks.length === 0" class="no-results">
        No cocktails found for "{{ searchQuery }}"
      </p>
    </div>
  </section>
</div>
