import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { CocktailService } from 'src/app/core/services/cocktail.service';

@Component({
  selector: 'app-cocktails',
  templateUrl: './cocktails.component.html',
  styleUrls: ['./cocktails.component.scss']
})
export class CocktailsComponent implements OnInit {
  allDrinks: any[] = [];
  filteredDrinks: any[] = [];
  cocktailName = '';
  searchQuery = '';
  sortBy = 'asc';

  filters = {
    alcoholic: false,
    nonAlcoholic: false
  };

  constructor(private cocktailService: CocktailService, private route: ActivatedRoute) {}

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      this.cocktailName = params['name'];
      this.cocktailService.getCocktailByName(this.cocktailName).subscribe((res: any) => {
        this.allDrinks = res.drinks || [];
        this.filteredDrinks = [...this.allDrinks];
      });
    });
  }

  applyFilters(): void {
    this.filteredDrinks = this.allDrinks.filter(drink => {
      const matchesSearch = this.searchQuery === '' || drink.strDrink.toLowerCase().includes(this.searchQuery.toLowerCase());
      const isAlcoholic = drink.strAlcoholic.toLowerCase().includes('alcoholic');
      const matchesAlcohol =
        (!this.filters.alcoholic && !this.filters.nonAlcoholic) ||
        (this.filters.alcoholic && isAlcoholic) ||
        (this.filters.nonAlcoholic && !isAlcoholic);
      return matchesSearch && matchesAlcohol;
    });

    this.filteredDrinks.sort((a, b) =>
      this.sortBy === 'asc'
        ? a.strDrink.localeCompare(b.strDrink)
        : b.strDrink.localeCompare(a.strDrink)
    );
  }
}
<div class="cocktails-page">
  <!-- Sidebar filter -->
  <aside class="filter-panel">
    <h3>Filter</h3>
    <label>
      <input type="checkbox" (change)="toggleFilter('Alcoholic')" /> Alcoholic
    </label>
    <label>
      <input type="checkbox" (change)="toggleFilter('Non_Alcoholic')" /> Non-Alcoholic
    </label>
  </aside>

  <!-- Main content -->
  <section class="content-panel">
    <div class="toolbar">
      <div class="sort-dropdown">
        <label>Sort by: </label>
        <select [(ngModel)]="sortOption" (change)="sortCocktails()">
          <option value="name-asc">Name ↑</option>
          <option value="name-desc">Name ↓</option>
        </select>
      </div>

      <div class="search-box" [class.expanded]="searchExpanded">
        <input
          *ngIf="searchExpanded"
          type="text"
          [(ngModel)]="searchTerm"
          placeholder="Search..."
          (keyup.enter)="searchCocktails()"
        />
        <button (click)="toggleSearch()">
          <i class="material-icons">search</i>
        </button>
      </div>
    </div>

    <!-- Cocktail cards -->
    <div class="cocktail-grid">
      <div class="card" *ngFor="let drink of filteredCocktails">
        <img [src]="drink.strDrinkThumb" alt="cocktail" />
        <h4>{{ drink.strDrink }}</h4>
        <p>{{ drink.strAlcoholic }} • {{ drink.strCategory }}</p>
      </div>
    </div>
  </section>
</div>
