import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  // Static cocktails for the home page cards
cocktails = [
  { name: 'Mojito', icon: 'üåø', gradient: 'linear-gradient(135deg, #a1c4fd, #c2e9fb)', font: `'Poppins', sans-serif` },
  { name: 'Margarita', icon: 'üçã', gradient: 'linear-gradient(135deg, #a1c4fd, #c2e9fb)', font: `'Poppins', sans-serif` },
  { name: 'Martini', icon: 'üç∏', gradient: 'linear-gradient(135deg, #a1c4fd, #c2e9fb)', font: `'Poppins', sans-serif` },
  { name: 'Manhattan', icon: 'ü•É', gradient: 'linear-gradient(135deg, #a1c4fd, #c2e9fb)', font: `'Poppins', sans-serif` },
  { name: 'Cosmopolitan', icon: 'üç∑', gradient: 'linear-gradient(135deg, #a1c4fd, #c2e9fb)', font: `'Poppins', sans-serif` },
  { name: 'Mai Tai', icon: 'üçç', gradient: 'linear-gradient(135deg, #a1c4fd, #c2e9fb)', font: `'Poppins', sans-serif` },
  { name: 'Pina Colada', icon: 'ü••', gradient: 'linear-gradient(135deg, #a1c4fd, #c2e9fb)', font: `'Poppins', sans-serif` },
  { name: 'Old Fashioned', icon: 'üßä', gradient: 'linear-gradient(135deg, #a1c4fd, #c2e9fb)', font: `'Poppins', sans-serif` }
];






  logo = 'CocktailForYou'; // Or use your logo image URL

  constructor(private router: Router) {}

  // Navigate to cocktail page when card is clicked
  goToCocktail(name: string) {
    this.router.navigate(['/cocktail', name]);
  }
}
<div class="home-container">
  <div class="card-grid">
    <div
      class="drink-card"
      *ngFor="let cocktail of cocktails"
      [ngStyle]="{
        'background-image': cocktail.gradient,
        'font-family': cocktail.font
      }"
      (click)="goToCocktail(cocktail.name)"
    >
      <div class="drink-icon">{{ cocktail.icon }}</div>
      <div class="drink-name">{{ cocktail.name }}</div>
    </div>
  </div>
</div>
